SELECT `item`.itemID, \n       COUNT(`votes`.itemID) AS `votes`,\n       `item`.title, \n       `item`.itemTypeID, \n       `item`.submitDate, \n       `item`.deleted, \n       `item`.ItemCat,\n       `item`.counter, \n       `item`.userID, \n       `users`.name,\n       TIMESTAMPDIFF(minute, `submitDate`, NOW()) AS 'timeMin',\n       `myItems`.userID AS userIDFav, \n       `myItems`.deleted AS myDeleted\nFROM item `item`\nLEFT JOIN votes `votes` ON `votes`.itemID = `item`.itemID\nINNER JOIN users `users` ON `users`.userID = `item`.userID\nLEFT JOIN myItems `myItems` ON `myItems`.itemID = `item`.itemID\nWHERE `item`.deleted = 0\nGROUP BY `item`.itemID\nHAVING userIDFav = 3 OR userIDFav IS NULL\nORDER BY `item`.itemID DESC\nLIMIT 20;