CREATE PROCEDURE SearchMovies\n        @SearchTerm1 NVARCHAR(50),\n        @SearchTerm2 NVARCHAR(50)\n    AS\n    BEGIN\n        SET NOCOUNT ON;\n        \n        DECLARE @SearchPattern1 NVARCHAR(100) = '"' + @SearchTerm1 + '*"';\n        DECLARE @SearchPattern2 NVARCHAR(100) = '"' + @SearchTerm2 + '*"';\n\n        SELECT TITLE\n        FROM MOVIES\n        WHERE CONTAINS(TITLE, @SearchPattern1)\n        AND CONTAINS(TITLE, @SearchPattern2);\n    END