DECLARE @SearchString NVARCHAR(100)\nSET @SearchString = 'YourSearchString'\n\nCREATE TABLE #Results (TableName NVARCHAR(256), ColumnName NVARCHAR(128), Value NVARCHAR(MAX))\n\nDECLARE @TableName NVARCHAR(256)\nDECLARE @ColumnName NVARCHAR(128)\nDECLARE @SQL NVARCHAR(MAX)\n\nDECLARE TableCursor CURSOR FOR\nSELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_TYPE = 'BASE TABLE'\n\nOPEN TableCursor\nFETCH NEXT FROM TableCursor INTO @TableName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    DECLARE ColumnCursor CURSOR FOR\n    SELECT COLUMN_NAME FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = @TableName AND DATA_TYPE IN ('char', 'varchar', 'nchar', 'nvarchar', 'text', 'ntext')\n\n    OPEN ColumnCursor\n    FETCH NEXT FROM ColumnCursor INTO @ColumnName\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        SET @SQL = 'INSERT INTO #Results (TableName, ColumnName, Value)\n                    SELECT ''' + @TableName + ''', ''' + @ColumnName + ''', [' + @ColumnName + ']\n                    FROM [' + @TableName + ']\n                    WHERE [' + @ColumnName + '] LIKE ''%' + @SearchString + '%'''\n\n        EXEC sp_executesql @SQL\n\n        FETCH NEXT FROM ColumnCursor INTO @ColumnName\n    END\n\n    CLOSE ColumnCursor\n    DEALLOCATE ColumnCursor\n\n    FETCH NEXT FROM TableCursor INTO @TableName\nEND\n\nCLOSE TableCursor\nDEALLOCATE TableCursor\n\nSELECT * FROM #Results\n\nDROP TABLE #Results