DECLARE @binaryString VARCHAR(MAX) = '0000100101000101011101011110';\nDECLARE @hexString VARCHAR(MAX) = '';\nDECLARE @chunk CHAR(4);\nDECLARE @i INT = 1;\n\nWHILE @i <= LEN(@binaryString)\nBEGIN\n    SET @chunk = SUBSTRING(@binaryString, @i, 4);\n\n    SET @hexString = @hexString + CASE @chunk\n        WHEN '0000' THEN '0'\n        WHEN '0001' THEN '1'\n        WHEN '0010' THEN '2'\n        WHEN '0011' THEN '3'\n        WHEN '0100' THEN '4'\n        WHEN '0101' THEN '5'\n        WHEN '0110' THEN '6'\n        WHEN '0111' THEN '7'\n        WHEN '1000' THEN '8'\n        WHEN '1001' THEN '9'\n        WHEN '1010' THEN 'A'\n        WHEN '1011' THEN 'B'\n        WHEN '1100' THEN 'C'\n        WHEN '1101' THEN 'D'\n        WHEN '1110' THEN 'E'\n        WHEN '1111' THEN 'F'\n        END;\n\n    SET @i = @i + 4;\nEND\n\nSELECT @hexString AS HexadecimalString;