CREATE TABLE Ship (\n    ID INT AUTO_INCREMENT PRIMARY KEY,\n    IMO INT NOT NULL UNIQUE,\n    -- Commonly accessed details directly in the Ship table\n    Name NVARCHAR(200),\n    Type NVARCHAR(50),\n    Flag NVARCHAR(50),\n    -- Other details remain in separate tables\n    CreatedAt DATETIME DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE ShipDetailType (\n    ID INT AUTO_INCREMENT PRIMARY KEY,\n    Description NVARCHAR(200),\n    Position SMALLINT,\n    ShipDetailGroup_ID INT,\n    Type VARCHAR(4)\n);\n\nCREATE TABLE ShipTextDetail (\n    ID INT AUTO_INCREMENT PRIMARY KEY,\n    Ship_ID INT,\n    ShipDetailType_ID INT,\n    Text NVARCHAR(500),\n    ModifiedDate DATETIME,\n    User_ID INT,\n    FOREIGN KEY (Ship_ID) REFERENCES Ship(ID),\n    FOREIGN KEY (ShipDetailType_ID) REFERENCES ShipDetailType(ID)\n);\n\nCREATE TABLE ShipTextDetailHistory (\n    ID INT AUTO_INCREMENT PRIMARY KEY,\n    ShipTextDetail_ID INT,\n    Text NVARCHAR(500),\n    ModifiedDate DATETIME,\n    User_ID INT,\n    FOREIGN KEY (ShipTextDetail_ID) REFERENCES ShipTextDetail(ID)\n);