USE [Database]\nGO\n/****** Object:  Trigger [dbo].[CopyFirstName]    Script Date: 07/15/2009 08:35:37 ******/\nSET ANSI_NULLS ON\nGO\nSET QUOTED_IDENTIFIER ON\nGO\n\nALTER TRIGGER [dbo].[CopyFirstName]\n   ON  [dbo].[Patients]\n   AFTER INSERT, UPDATE\nAS \nBEGIN\n    -- SET NOCOUNT ON added to prevent extra result sets from\n    -- interfering with SELECT statements.\n    SET NOCOUNT ON;\n\n    -- Update FirstName when First is updated\n    IF UPDATE([First])\n    BEGIN\n        UPDATE p\n        SET p.FirstName = i.[First]\n        FROM dbo.Patients p\n        INNER JOIN inserted i ON p.PrimaryKeyColumn = i.PrimaryKeyColumn\n    END\n\n    -- Update MiddleName when Middle is updated\n    IF UPDATE([Middle])\n    BEGIN\n        UPDATE p\n        SET p.MiddleName = i.[Middle]\n        FROM dbo.Patients p\n        INNER JOIN inserted i ON p.PrimaryKeyColumn = i.PrimaryKeyColumn\n    END\n\n    -- Update LastName when Last is updated\n    IF UPDATE([Last])\n    BEGIN\n        UPDATE p\n        SET p.LastName = i.[Last]\n        FROM dbo.Patients p\n        INNER JOIN inserted i ON p.PrimaryKeyColumn = i.PrimaryKeyColumn\n    END\nEND