SELECT tblAllDates.date, \n       NZ(STTData.STT, 0) AS STT, \n       NZ(VAData.VA, 0) AS VA\nFROM tblAllDates\nLEFT JOIN (\n    SELECT tblvolumedata.date, SUM(tblvolumedata.STT) AS STT\n    FROM tblvolumedata\n    GROUP BY tblvolumedata.date\n) AS STTData ON tblAllDates.date = STTData.date\nLEFT JOIN (\n    SELECT tblTimesheetData.date, \n           SUM(NZ(tblTimesheetData.batching) + NZ(tblTimesheetData.categorisation) +\n               NZ(tblTimesheetData.CDT) + NZ(tblTimesheetData.CSI) + \n               NZ(tblTimesheetData.destruction) + NZ(tblTimesheetData.extraction) + \n               NZ(tblTimesheetData.indexing) + NZ(tblTimesheetData.mail) + \n               NZ(tblTimesheetData.newlodgement) + NZ(tblTimesheetData.recordedDeliveries) + \n               NZ(tblTimesheetData.retrieval) + NZ(tblTimesheetData.scanning)) AS VA\n    FROM tblTimesheetData\n    GROUP BY tblTimesheetData.date\n) AS VAData ON tblAllDates.date = VAData.date\nORDER BY tblAllDates.date;