WITH CTE AS (\n    SELECT \n        MyField1,\n        MyField2,\n        MyField3,\n        MyField4,\n        MyField5,\n        ROW_NUMBER() OVER (PARTITION BY MyField1, MyField2, MyField3 ORDER BY MyField1, MyField2, MyField3) AS rn\n    FROM \n        TableA\n)\nSELECT \n    MyField1,\n    MyField2,\n    MyField3,\n    MyField4,\n    MyField5\nFROM \n    CTE\nWHERE \n    rn = 1;