DELIMITER //\n\nCREATE PROCEDURE ComputeStringChecksum()\nBEGIN\n    DECLARE done INT DEFAULT FALSE;\n    DECLARE concatenated_string TEXT DEFAULT '';\n    DECLARE cur CURSOR FOR SELECT some_column FROM your_table ORDER BY some_column;\n    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;\n\n    OPEN cur;\n\n    read_loop: LOOP\n        FETCH cur INTO @current_value;\n        IF done THEN\n            LEAVE read_loop;\n        END IF;\n        SET concatenated_string = CONCAT(concatenated_string, @current_value);\n    END LOOP;\n\n    CLOSE cur;\n\n    SELECT MD5(concatenated_string) AS checksum;\nEND //\n\nDELIMITER ;\n\nCALL ComputeStringChecksum();