DECLARE @sqlCommand NVARCHAR(MAX)\nDECLARE @params NVARCHAR(MAX)\nDECLARE @query NVARCHAR(MAX)\n\n-- Example input\nSET @sqlCommand = N'/*some query here*/'\nSET @params = N'@someParameter tinyint'\nDECLARE @someParameter tinyint\nSET @someParameter = 2\n\n-- Split parameters\nDECLARE @paramName NVARCHAR(255), @paramType NVARCHAR(255), @paramValue NVARCHAR(255)\nDECLARE @paramStart int, @paramEnd int\n\nSET @paramStart = CHARINDEX('@', @params)\nWHILE @paramStart > 0\nBEGIN\n    SET @paramEnd = CHARINDEX(' ', @params, @paramStart)\n    SET @paramName = SUBSTRING(@params, @paramStart, @paramEnd - @paramStart)\n    \n    SET @paramStart = @paramEnd + 1\n    SET @paramEnd = CHARINDEX(',', @params, @paramStart)\n    IF @paramEnd = 0 SET @paramEnd = LEN(@params) + 1\n    SET @paramType = SUBSTRING(@params, @paramStart, @paramEnd - @paramStart)\n    \n    -- Print declarations\n    PRINT 'DECLARE ' + @paramName + ' ' + @paramType\n    \n    -- Move to next parameter\n    SET @paramStart = CHARINDEX('@', @params, @paramEnd)\nEND\n\n-- Print parameter assignments\nPRINT 'SET @someParameter = 2'\n\n-- Print the original query\nPRINT @sqlCommand