USE [YourDatabase];\nGO\n\nDECLARE @DatabaseSizeMB DECIMAL(18,2);\n\nSELECT @DatabaseSizeMB = SUM(size * 8.0 / 1024) \nFROM sys.master_files \nWHERE database_id = DB_ID();\n\nPRINT 'Current database size in MB: ' + CAST(@DatabaseSizeMB AS NVARCHAR);\n\nIF @Edition LIKE '%Express%' AND @DatabaseSizeMB >= 4096\n    PRINT 'Warning: Database size is approaching or has exceeded the 4GB limit for SQL Server Express edition.';