WITH FullData AS (\n    SELECT \n        *,\n        ROW_NUMBER() OVER (ORDER BY your_timestamp_column) AS row_num\n    FROM \n        your_table\n)\nSELECT \n    *\nFROM \n    FullData\nWHERE \n    your_timestamp_column >= NOW() - INTERVAL 6 HOUR -- Last 6 hours\nUNION\nSELECT \n    *\nFROM \n    FullData\nWHERE \n    your_timestamp_column < NOW() - INTERVAL 6 HOUR AND row_num % 5 = 0; -- Older than 6 hours, every 5th row