-- Create tablespaces\nCREATE TABLESPACE fast_storage DATAFILE 'fast_storage.dbf' SIZE 50M AUTOEXTEND ON NEXT 10M MAXSIZE UNLIMITED;\nCREATE TABLESPACE archive_storage DATAFILE 'archive_storage.dbf' SIZE 50M AUTOEXTEND ON NEXT 10M MAXSIZE UNLIMITED;\n\n-- Create tables in different tablespaces\nCREATE TABLE myisam_like_table (\n    id NUMBER PRIMARY KEY,\n    data VARCHAR2(100)\n) TABLESPACE fast_storage;\n\nCREATE TABLE archive_table (\n    id NUMBER PRIMARY KEY,\n    data VARCHAR2(100),\n    archived_date DATE\n) TABLESPACE archive_storage;\n\n-- Example of creating an external table for test purposes\nCREATE TABLE external_table (\n    id NUMBER,\n    data VARCHAR2(100)\n)\nORGANIZATION EXTERNAL (\n    TYPE ORACLE_LOADER\n    DEFAULT DIRECTORY my_dir\n    ACCESS PARAMETERS (\n        RECORDS DELIMITED BY NEWLINE\n        FIELDS TERMINATED BY ','\n        (id, data)\n    )\n    LOCATION ('external_data.csv')\n);\n\n-- Example of using a materialized view\nCREATE MATERIALIZED VIEW my_materialized_view\nBUILD IMMEDIATE\nREFRESH COMPLETE ON DEMAND\nAS\nSELECT * FROM myisam_like_table;