-- On the Publisher (Main SQL Server)\nUSE master;\nEXEC sp_replicationdboption \n    @dbname = 'MainDB', \n    @optname = 'publish', \n    @value = 'true';\n\n-- Create a publication\nEXEC sp_addpublication \n    @publication = 'MainDBPublication', \n    @status = N'active';\n\n-- Add articles (tables) to the publication\nEXEC sp_addarticle \n    @publication = 'MainDBPublication', \n    @article = 'Orders', \n    @source_table = 'Orders';\n\n-- On the Subscriber (Brand-Specific SQL Server)\n-- Create a subscription to the publication\nEXEC sp_addsubscription \n    @publication = 'MainDBPublication', \n    @subscriber = 'BrandSQLServer', \n    @destination_db = 'BrandDB';\n\n-- Initialize the subscription\nEXEC sp_addpushsubscription_agent \n    @publication = 'MainDBPublication', \n    @subscriber = 'BrandSQLServer', \n    @subscriber_db = 'BrandDB', \n    @job_login = 'joblogin', \n    @job_password = 'password';\n\n-- Ensure data consistency\nEXEC sp_startpublication_snapshot \n    @publication = 'MainDBPublication';