WITH RECURSIVE ParentCTE AS (\n    -- Anchor member: start with the given child\n    SELECT id, name, parentid, value\n    FROM example\n    WHERE id = 1  -- Replace 1 with the ID of the child you are interested in\n\n    UNION ALL\n\n    -- Recursive member: find the parent of the current row\n    SELECT e.id, e.name, e.parentid, e.value\n    FROM example e\n    INNER JOIN ParentCTE p ON e.id = p.parentid\n)\n-- Select the top parent (the row with no parent)\nSELECT * \nFROM ParentCTE \nWHERE parentid IS NULL;