-- Main Table\nCREATE TABLE MainTable (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    name VARCHAR(255) NOT NULL\n);\n\n-- Linker Table\nCREATE TABLE LinkerTable (\n    parent_id INT,\n    child_id INT,\n    PRIMARY KEY (parent_id, child_id),\n    FOREIGN KEY (parent_id) REFERENCES MainTable(id) ON DELETE CASCADE,\n    FOREIGN KEY (child_id) REFERENCES MainTable(id) ON DELETE CASCADE\n);