SELECT 'Status' AS Status, * \nFROM WorkItems t1\nWHERE EXISTS (\n    SELECT 1 \n    FROM workitems t2 \n    WHERE t1.TextField01 = t2.TextField01 AND BoolField05 = 1\n) \nAND TimeStamp = (\n    SELECT MAX(t2.TimeStamp) \n    FROM workitems t2 \n    WHERE t2.TextField01 = t1.TextField01\n) \nAND TimeStamp > '2009-02-12 18:00:00'\n\nUNION ALL\n\nSELECT 'DELETED' AS Status, * \nFROM WorkItems t1\nWHERE EXISTS (\n    SELECT 1 \n    FROM workitems t2 \n    WHERE t1.TextField01 = t2.TextField01 AND BoolField05 = 1\n) \nAND TimeStamp = (\n    SELECT MAX(t2.TimeStamp) \n    FROM workitems t2 \n    WHERE t2.TextField01 = t1.TextField01\n) \nAND TimeStamp > '2009-02-12 18:00:00'\nAND NOT (BoolField05 = 1);