-- Base table\nCREATE TABLE media (\n    id SERIAL PRIMARY KEY,\n    name VARCHAR(100),\n    description TEXT,\n    url VARCHAR(255)\n);\n\n-- Specialized table for tv_series\nCREATE TABLE tv_series (\n    id INTEGER PRIMARY KEY,\n    season INTEGER,\n    episode INTEGER,\n    airing DATE,\n    FOREIGN KEY (id) REFERENCES media (id)\n);\n\n-- Specialized table for movies\nCREATE TABLE movies (\n    id INTEGER PRIMARY KEY,\n    release_date DATE,\n    budget NUMERIC,\n    FOREIGN KEY (id) REFERENCES media (id)\n);\n\n-- Example INSERTs\nINSERT INTO media (name, description, url) \nVALUES ('Breaking Bad', 'A high school chemistry teacher turned methamphetamine producer.', 'http://example.com');\nINSERT INTO tv_series (id, season, episode, airing) \nVALUES (1, 5, 14, '2013-09-29');\n\nINSERT INTO media (name, description, url) \nVALUES ('Inception', 'A thief who steals corporate secrets through the use of dream-sharing technology.', 'http://example.com');\nINSERT INTO movies (id, release_date, budget) \nVALUES (2, '2010-07-16', 160000000);