DECLARE @OLD_NAV_VALUE AS INT\nDECLARE @FINAL AS INT\nDECLARE @SQL AS NVARCHAR(MAX)\nDECLARE @DYNAMIC_COL_NAME AS NVARCHAR(100) = 'col1' -- Example column name\nDECLARE @id_Date AS NVARCHAR(10) = '2021-01-01'    -- Example date\n\n-- Construct the dynamic SQL statement\nSET @SQL = N'SELECT @OLD_NAV_VALUE = [' + @DYNAMIC_COL_NAME + '] FROM TBL_BA WHERE DATE = @id_Date'\n\n-- Execute the dynamic SQL with output parameter\nEXEC sp_executesql \n    @SQL, \n    N'@OLD_NAV_VALUE INT OUTPUT, @id_Date NVARCHAR(10)', \n    @OLD_NAV_VALUE OUTPUT, \n    @id_Date\n\n-- Use the value of @OLD_NAV_VALUE\nSET @FINAL = @OLD_NAV_VALUE * 50\n\n-- Output the result for verification\nSELECT @FINAL AS FINAL_RESULT