-- Insert example\nINSERT INTO MYTABLE (NAME, STATUS, UPDATE_COUNT)\nSELECT 'John', 0, IFNULL(MAX(UPDATE_COUNT), 0) + 1 FROM MYTABLE;\n\n-- Update example\nUPDATE MYTABLE\nSET STATUS = 1, UPDATE_COUNT = (SELECT MAX_UPDATE_COUNT + 1 FROM (SELECT MAX(UPDATE_COUNT) AS MAX_UPDATE_COUNT FROM MYTABLE) AS subquery)\nWHERE NAME = 'John';