CREATE TABLE media (\n    id SERIAL PRIMARY KEY,\n    type VARCHAR(50), -- 'tv_series' or 'movie'\n    name VARCHAR(100),\n    description TEXT,\n    url VARCHAR(255),\n    season INTEGER,  -- specific to tv_series\n    episode INTEGER, -- specific to tv_series\n    airing DATE,     -- specific to tv_series\n    release_date DATE, -- specific to movies\n    budget NUMERIC    -- specific to movies\n);\n\n-- Example INSERTs\nINSERT INTO media (type, name, description, url, season, episode, airing) \nVALUES ('tv_series', 'Breaking Bad', 'A high school chemistry teacher turned methamphetamine producer.', 'http://example.com', 5, 14, '2013-09-29');\n\nINSERT INTO media (type, name, description, url, release_date, budget) \nVALUES ('movie', 'Inception', 'A thief who steals corporate secrets through the use of dream-sharing technology.', 'http://example.com', '2010-07-16', 160000000);