BEGIN\n    DBMS_XMLSCHEMA.registerSchema(\n        schemaURL => 'http://www.example.com/my_schema.xsd',\n        schemaDoc => bfilename('MY_DIR', 'my_schema.xsd'),\n        local => TRUE,\n        genTypes => FALSE,\n        genTables => FALSE\n    );\nEND;\n/\n\nCREATE TABLE xml_data (\n    id NUMBER PRIMARY KEY,\n    xml_content XMLType\n) XMLType COLUMN xml_content STORE AS OBJECT RELATIONAL XMLSCHEMA "http://www.example.com/my_schema.xsd";