CREATE TABLE JobPostings (\n    JobPostingID INT PRIMARY KEY,\n    UserID INT,\n    Company NVARCHAR(255),\n    JobTitle NVARCHAR(255),\n    JobTypeID INT,\n    JobLocationID INT,\n    JobDescription TEXT,\n    HowToApply NVARCHAR(1000),\n    CompanyURL NVARCHAR(255),\n    LogoURL NVARCHAR(255),\n    FOREIGN KEY (UserID) REFERENCES Users(UserID),\n    FOREIGN KEY (JobTypeID) REFERENCES JobTypes(JobTypeID),\n    FOREIGN KEY (JobLocationID) REFERENCES JobLocations(JobLocationID)\n);\n\nCREATE TABLE JobLocations (\n    JobLocationID INT PRIMARY KEY,\n    City NVARCHAR(255),\n    State NVARCHAR(255),\n    Zip NVARCHAR(20)\n);\n\nCREATE TABLE JobTypes (\n    JobTypeID INT PRIMARY KEY,\n    JobTypeName NVARCHAR(255)\n);