CREATE TABLE MyEntity (\n       Id INT PRIMARY KEY IDENTITY,\n       Name NVARCHAR(50) NOT NULL CHECK (LEN(Name) BETWEEN 5 AND 50),\n       LastModified DATETIME NOT NULL DEFAULT GETDATE()\n   );\n\n   CREATE TRIGGER trg_UpdateLastModified\n   ON MyEntity\n   AFTER UPDATE\n   AS\n   BEGIN\n       SET NOCOUNT ON;\n       UPDATE MyEntity\n       SET LastModified = GETDATE()\n       FROM inserted\n       WHERE MyEntity.Id = inserted.Id;\n   END;