-- Create the object type at the schema level\nCREATE OR REPLACE TYPE my_type AS OBJECT (\n    id NUMBER(15)\n);\n/\n\n-- Create the package that uses the object type\nCREATE OR REPLACE PACKAGE my_package AS\n    -- You can use the object type here\n    PROCEDURE use_my_type(p_obj IN my_type);\nEND my_package;\n/\n\n-- Package body with implementation\nCREATE OR REPLACE PACKAGE BODY my_package AS\n    PROCEDURE use_my_type(p_obj IN my_type) IS\n    BEGIN\n        -- Implementation code here\n        DBMS_OUTPUT.PUT_LINE('ID: ' || p_obj.id);\n    END use_my_type;\nEND my_package;\n/