-- Anti-pattern\n   DECLARE cursor_name CURSOR FOR\n   SELECT id, name FROM employees;\n   OPEN cursor_name;\n   FETCH NEXT FROM cursor_name INTO @id, @name;\n   WHILE @@FETCH_STATUS = 0\n   BEGIN\n       -- Process each row\n       FETCH NEXT FROM cursor_name INTO @id, @name;\n   END\n   CLOSE cursor_name;\n   DEALLOCATE cursor_name;\n\n   -- Better approach\n   UPDATE employees\n   SET processed = 1\n   WHERE condition;