-- Create the trigger on the server\nCREATE TRIGGER PreventCertainCommands\nON ALL SERVER\nFOR DROP_DATABASE, DROP_TABLE, DROP_PROCEDURE, DROP_INDEX, DROP_VIEW, DELETE, UPDATE\nAS\nBEGIN\n    -- List of users to restrict\n    DECLARE @userList TABLE (username SYSNAME);\n    INSERT INTO @userList (username)\n    VALUES ('DOMAIN\RestrictedUser1'), ('DOMAIN\RestrictedUser2'); -- Add more users as needed\n\n    -- Check if the current user is in the restricted list\n    IF EXISTS (SELECT 1 FROM @userList WHERE username = ORIGINAL_LOGIN())\n    BEGIN\n        ROLLBACK; -- Rollback the attempted operation\n        THROW 50001, 'You do not have permission to perform this operation.', 1; -- Raise an error\n    END\nEND;\nGO