-- Full Backup (Weekly)\nBACKUP DATABASE [YourDatabaseName] \nTO DISK = 'D:\Backups\YourDatabaseName_Full.bak'\nWITH INIT, NAME = 'Full Backup of YourDatabaseName';\n\n-- Differential Backup (Daily)\nBACKUP DATABASE [YourDatabaseName] \nTO DISK = 'D:\Backups\YourDatabaseName_Diff.bak'\nWITH DIFFERENTIAL, INIT, NAME = 'Differential Backup of YourDatabaseName';\n\n-- Transaction Log Backup (Every 4-6 hours)\nBACKUP LOG [YourDatabaseName] \nTO DISK = 'D:\Backups\YourDatabaseName_Log.bak'\nWITH INIT, NAME = 'Transaction Log Backup of YourDatabaseName';