DELIMITER //\n\nCREATE PROCEDURE SearchReplace(IN tbl_name VARCHAR(64), IN column_name VARCHAR(64), IN search_text VARCHAR(255), IN replace_text VARCHAR(255))\nBEGIN\n    SET @sql = CONCAT('UPDATE ', tbl_name, ' SET ', column_name, ' = REPLACE(', column_name, ', "', search_text, '", "', replace_text, '")');\n    PREPARE stmt FROM @sql;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\nEND //\n\nDELIMITER ;