BEGIN;\n\n-- Lock the row to ensure exclusive access\nSELECT * FROM your_table WHERE id = <row_id> FOR UPDATE;\n\n-- Perform the update\nUPDATE your_table\nSET boolean_column = TRUE\nWHERE id = <row_id> AND boolean_column = FALSE;\n\n-- Check the number of affected rows to ensure the update occurred\nIF FOUND THEN\n    -- Perform your action here\n    PERFORM do_some_action();\nEND IF;\n\nCOMMIT;