-- Example of a linking table\nCREATE TABLE myTable (ID INT PRIMARY KEY);\nCREATE TABLE myRelatedTable (ID INT PRIMARY KEY, myTableID INT);\nCREATE TABLE myLinkTable (myRelatedTableID INT, myTableID INT);\n\n-- Insert example data\nINSERT INTO myTable (ID) VALUES (1), (2), (3);\nINSERT INTO myRelatedTable (ID, myTableID) VALUES (1, NULL);\n\n-- Linking all rows explicitly\nINSERT INTO myLinkTable (myRelatedTableID, myTableID)\nSELECT myRelatedTable.ID, myTable.ID\nFROM myRelatedTable, myTable\nWHERE myRelatedTable.myTableID IS NULL;