SELECT \n    us_sender.name AS sender_name, \n    us_recipient.name AS recipient_name, \n    ta.amount, \n    ta.recipient_id, \n    ta.sender_id, \n    ta.timestamp_insert\nFROM \n    `transactions` AS ta\nJOIN \n    users AS us_sender\nON \n    ta.sender_id = us_sender.u_id\nJOIN \n    users AS us_recipient\nON \n    ta.recipient_id = us_recipient.u_id\nWHERE \n    ta.sender_id = 111111 OR ta.recipient_id = 111111\nLIMIT \n    0, 10;