-- RestaurantChains Table\nCREATE TABLE RestaurantChains (\n    ChainID INT PRIMARY KEY,\n    ChainName VARCHAR(255) NOT NULL\n);\n\n-- Restaurants Table\nCREATE TABLE Restaurants (\n    RestaurantID INT PRIMARY KEY,\n    ChainID INT,\n    RestaurantName VARCHAR(255) NOT NULL,\n    FOREIGN KEY (ChainID) REFERENCES RestaurantChains(ChainID)\n);\n\n-- MenuItems Table\nCREATE TABLE MenuItems (\n    MenuItemID INT PRIMARY KEY,\n    RestaurantID INT,\n    ItemName VARCHAR(255) NOT NULL,\n    FOREIGN KEY (RestaurantID) REFERENCES Restaurants(RestaurantID)\n);\n\n-- Comments Table\nCREATE TABLE Comments (\n    CommentID INT PRIMARY KEY,\n    CommentText TEXT NOT NULL,\n    OwnerType VARCHAR(50) NOT NULL,\n    OwnerID INT NOT NULL\n);\n\n-- Ratings Table\nCREATE TABLE Ratings (\n    RatingID INT PRIMARY KEY,\n    RatingValue INT NOT NULL,\n    OwnerType VARCHAR(50) NOT NULL,\n    OwnerID INT NOT NULL\n);\n\n-- Linking Table for Comments\nCREATE TABLE CommentLinks (\n    CommentLinkID INT PRIMARY KEY,\n    CommentID INT,\n    OwnerType VARCHAR(50),\n    OwnerID INT,\n    FOREIGN KEY (CommentID) REFERENCES Comments(CommentID)\n);\n\n-- Linking Table for Ratings\nCREATE TABLE RatingLinks (\n    RatingLinkID INT PRIMARY KEY,\n    RatingID INT,\n    OwnerType VARCHAR(50),\n    OwnerID INT,\n    FOREIGN KEY (RatingID) REFERENCES Ratings(RatingID)\n);