exec sp_executesql \nN'SELECT [t0].[HomeID], \n  [t0].[Bedrooms], \n  [t0].[ImageURL], \n  [t0].[Price], \n  [t0].[Available], \n  [t0].[Description]\nFROM \n  [dbo].[Homes] AS [t0]\nWHERE \n  ([t0].[Description] LIKE @p0) AND \n    ([t0].[Available] = @p1) AND \n    ([t0].[Price] >= @p2) AND ([t0].[Price] <= @p3)\nORDER BY \n  [t0].[Price] DESC',\nN'@p0 nvarchar(4000),@p1 int,@p2 int,@p3 int',\n@p0=N'%private%',\n@p1=1,\n@p2=200000,\n@p3=750000