CREATE OR REPLACE FUNCTION MY_FUNCTION (myChar IN CHAR) RETURN INTEGER AS\n    TYPE ref_cursor IS REF CURSOR;\n    c_ref ref_cursor;\n    v_name VARCHAR2(100);\nBEGIN\n    IF myChar = 'A' THEN\n        OPEN c_ref FOR SELECT FIRST_NAME FROM EMPLOYEES;\n    ELSE\n        OPEN c_ref FOR SELECT LAST_NAME FROM EMPLOYEES;\n    END IF;\n\n    LOOP\n        FETCH c_ref INTO v_name;\n        EXIT WHEN c_ref%NOTFOUND;\n        -- DO STUFF with v_name\n    END LOOP;\n\n    CLOSE c_ref;\n    RETURN 1;\nEND MY_FUNCTION;\n/