WITH FirstSupportCases AS (\n    SELECT\n        ID,\n        Case_ID,\n        Name,\n        Date,\n        Status,\n        Type,\n        ROW_NUMBER() OVER (PARTITION BY Case_ID ORDER BY Date) AS RowNumber\n    FROM YourTable\n    WHERE Type = 'Support' AND Status = 'Start'\n)\n\nSELECT\n    ID,\n    Case_ID,\n    Name,\n    Date,\n    Status,\n    Type\nFROM FirstSupportCases\nWHERE RowNumber = 1\n\nUNION\n\nSELECT\n    ID,\n    Case_ID,\n    Name,\n    Date,\n    Status,\n    Type\nFROM YourTable\nWHERE \n    Case_ID IN (SELECT Case_ID FROM CaseTable WHERE Date BETWEEN '2007-11-01' AND '2007-11-30')\n    AND Type = 'Support'\n    AND Status != 'Start'\n    AND NOT EXISTS (\n        SELECT 1\n        FROM FirstSupportCases\n        WHERE FirstSupportCases.Case_ID = YourTable.Case_ID\n    );