CREATE TABLE FinalMovies AS\n   SELECT \n       m.UniqueName, \n       m.Title, \n       m.Year, \n       r.MPAA_Rating, \n       r.Reason, \n       t.RunningTime\n   FROM \n       StagingMovies m\n   LEFT JOIN \n       StagingMPAARatings r ON m.UniqueName = r.UniqueName\n   LEFT JOIN \n       StagingRunningTimes t ON m.UniqueName = t.UniqueName\n   WHERE \n       m.Title NOT LIKE '%(VG)' AND m.Title NOT LIKE '%(TV)';