-- Create a function to compute SHA-256 hash\nCREATE FUNCTION dbo.ComputeSHA256(@input VARCHAR(2000))\nRETURNS CHAR(64)\nAS\nBEGIN\n    DECLARE @hash VARBINARY(32);\n    DECLARE @hashString CHAR(64);\n    \n    -- Compute the hash\n    SET @hash = HASHBYTES('SHA2_256', @input);\n    \n    -- Convert the hash to a hexadecimal string\n    SET @hashString = CONVERT(CHAR(64), @hash, 2);\n    \n    RETURN @hashString;\nEND;\n\n-- Example of using the function in a query\nSELECT \n    COUNT(1),\n    dbo.ComputeSHA256([user]) AS user_hash\nFROM\n    LogTable\nWHERE \n    date BETWEEN @from AND @to\nGROUP BY \n    dbo.ComputeSHA256([user]);