CREATE OR REPLACE PROCEDURE usp_dropTable(schema VARCHAR, tblToDrop VARCHAR) \nAUTHID CURRENT_USER IS\nBEGIN\n  DECLARE v_cnt NUMBER;\n  BEGIN\n    SELECT COUNT(*) \n      INTO v_cnt \n      FROM all_tables \n     WHERE owner = UPPER(schema)\n       AND table_name = UPPER(tblToDrop);\n\n     IF v_cnt > 0 THEN \n        EXECUTE IMMEDIATE 'DROP TABLE ' || schema || '.' || tblToDrop || ' PURGE';\n     END IF;\n   END;\nEND;