-- Select the data to verify the current mapping\nSELECT sp.samplepointid, od.oildatasetid, os.oildatasetstatusid\nFROM samplepoint sp\nJOIN oildataset od ON sp.samplepointid = od.samplepointid\nJOIN Oildatasetstatus os ON od.oildatasetid = os.oildatasetid\nWHERE od.oillabid = 4\n  AND os.oildatasetstatusid IN (5, 6, 7, 8, 9, 10, 11);\n\n-- Update the severity mapping\nUPDATE Oildatasetstatus os\nSET oildatasetstatusid = CASE\n    WHEN oildatasetstatusid = 5 THEN 16\n    WHEN oildatasetstatusid = 6 THEN 17\n    WHEN oildatasetstatusid IN (7, 8, 9) THEN 18\n    WHEN oildatasetstatusid = 10 THEN 19\n    WHEN oildatasetstatusid = 11 THEN 20\nEND\nWHERE EXISTS (\n    SELECT 1\n    FROM samplepoint sp\n    JOIN oildataset od ON sp.samplepointid = od.samplepointid\n    WHERE od.oildatasetid = os.oildatasetid\n      AND od.oillabid = 4\n)\n  AND os.oildatasetstatusid IN (5, 6, 7, 8, 9, 10, 11);