CREATE PROCEDURE SAFE_DROP_TABLE\n    @TableName NVARCHAR(128)\nAS\nBEGIN\n    DECLARE @SQL NVARCHAR(MAX);\n    DECLARE @Error INT;\n\n    -- Check if the table exists\n    IF EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(@TableName) AND type = 'U')\n    BEGIN\n        -- Construct the dynamic SQL to drop the table\n        SET @SQL = 'DROP TABLE ' + QUOTENAME(@TableName);\n\n        -- Execute the dynamic SQL\n        EXEC sp_executesql @SQL;\n\n        -- Check for errors\n        SET @Error = @@ERROR;\n        IF @Error <> 0\n        BEGIN\n            PRINT 'Failed to drop ' + @TableName + '. Error = ' + CAST(@Error AS NVARCHAR(10));\n        END\n        ELSE\n        BEGIN\n            PRINT 'Dropped ' + @TableName;\n        END\n    END\n    ELSE\n    BEGIN\n        PRINT 'No action needed. Table ' + @TableName + ' does not exist.';\n    END\nEND;