USE tempdb;\nGO\n\nCREATE TABLE Tbl(\n    TblID INT,\n    AuditFieldID INT,\n    AuditValue INT,\n    AuditDate DATE\n);\nGO\n\nINSERT INTO Tbl(TblID, AuditFieldID, AuditValue, AuditDate) VALUES\n(1, 10, 101, '2001-01-01'),\n(2, 10, 102, '2002-01-01'),\n(3, 20, 201, '2001-01-01'),\n(4, 20, 202, '2009-01-01');\nGO\n\nWITH RankedTbl AS (\n    SELECT \n        AuditFieldID,\n        AuditValue,\n        AuditDate,\n        ROW_NUMBER() OVER (PARTITION BY AuditFieldID ORDER BY AuditDate DESC) AS rn\n    FROM Tbl\n)\nSELECT \n    AuditFieldID,\n    AuditValue,\n    AuditDate\nFROM \n    RankedTbl\nWHERE \n    rn = 1;