SELECT \n    FROM_TZ(CAST(SYSDATE AS TIMESTAMP), 'America/New_York') AS current_time,\n    TZ_OFFSET('America/New_York') AS current_offset,\n    TZ_OFFSET('America/New_York') - TZ_OFFSET('UTC') AS offset_difference,\n    CASE\n        WHEN TZ_OFFSET('America/New_York') <> \n             TZ_OFFSET('America/New_York' AT TIME ZONE 'UTC') THEN 'DST is in effect'\n        ELSE 'Standard time is in effect'\n    END AS dst_status\nFROM dual;