-- Step 1: Check join between tables a and b\n   SELECT * \n   FROM a \n   JOIN b ON a.b_id = b.id;\n\n   -- Step 2: Check join between the result of (a JOIN b) and table c\n   SELECT * \n   FROM a \n   JOIN b ON a.b_id = b.id\n   JOIN c ON b.c_id = c.id;\n\n   -- Step 3: Check join between the result of (a JOIN b JOIN c) and table d\n   SELECT * \n   FROM a \n   JOIN b ON a.b_id = b.id\n   JOIN c ON b.c_id = c.id\n   JOIN d ON c.d_id = d.id;