-- Create a temporary table\nCREATE TABLE #Protocols (ProtocolID int NOT NULL)\n\n-- Insert the result of the subquery into the temporary table\nINSERT INTO #Protocols\nSELECT ProtocolID\nFROM dbo.Protocols\nWHERE WorkplaceID = @WorkplaceID\n\n-- Use the temporary table to delete rows in other tables\nDELETE FROM dbo.SubProtocols\nWHERE ProtocolID IN (SELECT ProtocolID FROM #Protocols)\n\nDELETE FROM dbo.ProtocolHeaders\nWHERE ProtocolID IN (SELECT ProtocolID FROM #Protocols)\n\n-- Repeat for other tables as necessary\n-- ...\n\n-- Finally, delete from the Protocols table\nDELETE FROM dbo.Protocols\nWHERE WorkplaceID = @WorkplaceID\n\n-- Drop the temporary table\nDROP TABLE #Protocols