-- Example of an audit table\n   CREATE TABLE dbo.BillAdjustmentsAudit (\n       AuditID INT IDENTITY(1,1) PRIMARY KEY,\n       BillID INT,\n       OldValue VARCHAR(255),\n       NewValue VARCHAR(255),\n       ChangeDate DATETIME DEFAULT GETDATE(),\n       ChangedBy VARCHAR(255)\n   );\n\n   -- Trigger to capture changes\n   CREATE TRIGGER trg_AfterUpdate_BillAdjustments\n   ON dbo.BillAdjustments\n   AFTER UPDATE\n   AS\n   BEGIN\n       INSERT INTO dbo.BillAdjustmentsAudit (BillID, OldValue, NewValue, ChangedBy)\n       SELECT \n           i.BillID, \n           d.OldValue, \n           i.NewValue, \n           SYSTEM_USER\n       FROM \n           Inserted i\n       JOIN \n           Deleted d ON i.BillID = d.BillID;\n   END;