DECLARE\n    v_columns VARCHAR2(4000);\nBEGIN\n    -- Fetch the column names from the PRIMARY_TABLE\n    SELECT LISTAGG(column_name, ', ') WITHIN GROUP (ORDER BY column_id)\n    INTO v_columns\n    FROM all_tab_columns\n    WHERE table_name = 'PRIMARY_TABLE'\n    AND owner = 'YOUR_SCHEMA'; -- Replace with your schema name\n\n    -- Execute the insert statement with the dynamic column list\n    EXECUTE IMMEDIATE 'INSERT INTO BACKUP_TABLE (' || v_columns || ') SELECT ' || v_columns || ' FROM PRIMARY_TABLE';\nEND;\n/