-- Code Block 1 equivalent\nSELECT TOP 50 \n    OBJECT_NAME(m.[object_id]) AS [ProcedureName], \n    m.[definition]\nFROM \n    sys.sql_modules m\nJOIN \n    sys.objects o ON m.[object_id] = o.[object_id]\nWHERE \n    m.[definition] LIKE '%09/01/2008%' \n    AND o.[name] LIKE '%NET';\n\n-- Code Block 2 equivalent\nSELECT \n    OBJECT_NAME(m.[object_id]) AS [ProcedureName], \n    m.[definition]\nFROM \n    sys.sql_modules m\nJOIN \n    sys.objects o ON m.[object_id] = o.[object_id]\nWHERE \n    m.[definition] LIKE '%EffectiveDate%' \n    AND o.[type] = 'P'  -- 'P' stands for Stored Procedure\n    AND o.[name] LIKE '%NET';