CREATE OR REPLACE PROCEDURE TEST_EXCEPTION\n(\n    book_id_in IN book.book_id%TYPE,\n    emp_id_in IN emp.emp_id%TYPE\n)\nIS\n\nv_book_desc book.description%TYPE;\nv_emp_name emp.emp_name%TYPE;\n\nBEGIN\n    BEGIN\n        SELECT description INTO v_book_desc \n        FROM book \n        WHERE book_id = book_id_in;\n    EXCEPTION\n        WHEN NO_DATA_FOUND THEN\n            DBMS_OUTPUT.PUT_LINE('No data found for book_id: ' || book_id_in);\n            RETURN;\n    END;\n\n    BEGIN\n        SELECT emp_name INTO v_emp_name \n        FROM emp \n        WHERE emp_id = emp_id_in;\n    EXCEPTION\n        WHEN NO_DATA_FOUND THEN\n            DBMS_OUTPUT.PUT_LINE('No data found for emp_id: ' || emp_id_in);\n            RETURN;\n    END;\n\n    -- Continue with the rest of your code\n    ...\n\nEXCEPTION\n    WHEN OTHERS THEN\n        DBMS_OUTPUT.PUT_LINE('An unexpected error occurred: ' || SQLERRM);\nEND TEST_EXCEPTION;