-- Create a new table with separate columns for each data type\nCREATE TABLE YourNewTable (\n    ID INT PRIMARY KEY,\n    IntValue INT NULL,\n    StringValue VARCHAR(255) NULL,\n    DateTimeValue DATETIME NULL\n);\n\n-- Example of inserting data into the new table\nINSERT INTO YourNewTable (ID, IntValue)\nVALUES (1000, 10);\n\nINSERT INTO YourNewTable (ID, StringValue)\nVALUES (1001, 'foo');\n\nINSERT INTO YourNewTable (ID, DateTimeValue)\nVALUES (1002, '2008-12-10');